import{aw as a,A as i,aP as l,z as t}from"./chunks/framework.CPxOa8kE.js";const f=JSON.parse('{"title":"组件库介绍","description":"","frontmatter":{},"headers":[],"relativePath":"zh-CN/index.md","filePath":"zh-CN/index.md"}'),r={name:"zh-CN/index.md"};function o(n,e,s,d,h,c){return t(),i("div",null,e[0]||(e[0]=[l('<h1 id="组件库介绍" tabindex="-1">组件库介绍 <a class="header-anchor" href="#组件库介绍" aria-label="Permalink to &quot;组件库介绍&quot;">​</a></h1><h2 id="why-为什么封装此组件库" tabindex="-1">Why - 为什么封装此组件库 <a class="header-anchor" href="#why-为什么封装此组件库" aria-label="Permalink to &quot;Why - 为什么封装此组件库&quot;">​</a></h2><p>本组件库旨在封装日常开发中常用的一些基于Element Plus的扩展组件。</p><h2 id="what-封装内容" tabindex="-1">What - 封装内容 <a class="header-anchor" href="#what-封装内容" aria-label="Permalink to &quot;What - 封装内容&quot;">​</a></h2><ul><li>Alias，优化多重循环，定义临时变量，方便模板中以简洁的方式访问临时变量</li><li>AvatarList，一组用户头像，常用在项目/团队成员列表</li><li>Banner，横幅通常用于标识全页的状态或通知等</li><li>ConfirmableButton，通常用于需要再次确认的操作</li><li>DockContainer，主要用来收纳折叠起来的ModelessDialog</li><li><em><strong>ModelessDialog，非模态对话框，支持最大化，最小化，收起，展开，拖拽改变大小等功能</strong></em></li><li>OverflowList，通常用于表格操作列，当操作项比较多时，可利用此组件进行操作的显隐</li><li>Panel，面板组件，类似于Card组件，但更灵活多变</li><li>SimpleTable，非常轻量的表格组件，只使用<code>HTML table</code>实现常见功能</li><li>Spinner，展示一个 “加载中” 的工作状态</li><li>TextOverflow，通过设置文本长度或者组件width使文本出现溢出效果</li><li>TreeTransfer，对树形结构进行选择操作</li></ul>',5)]))}const p=a(r,[["render",o]]);export{f as __pageData,p as default};
